class PercentageBar extends HTMLElement{connectedCallback(){const e=this.getAttribute("value")||"0",n=this.getAttribute("label")||"Unknown";this.innerHTML=`\n      <div class="mb-4">\n        <div class="flex justify-between mb-1">\n          <span>${n}</span><span class="text-sm text-gray-500">${e}%</span>\n        </div>\n        <div class="w-full bg-gray-200 rounded-full h-2.5">\n          <div class="bg-blue-500 h-2.5 rounded-full" style="width: ${e}%"></div>\n        </div>\n      </div>\n    `}}customElements.define("percentage-bar",PercentageBar);