class StatCard extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"})}connectedCallback(){const n=this.getAttribute("value")||"0",t=this.getAttribute("label")||"Unknown";this.shadowRoot.innerHTML=`\n      <style>\n        .card {\n          background-color: white;\n          padding: 1rem;\n          border-radius: 0.5rem;\n          box-shadow: 0 3px 3px rgba(0,0,0,0.1);\n          text-align: center;\n        }\n        .value {\n          font-size: 1.25rem;\n          font-weight: bold;\n        }\n        .label {\n          color: #4b5563;\n          margin-top: 0.25rem;\n        }\n      </style>\n      <div class="card">\n        <p class="value">${n}</p>\n        <p class="label">${t}</p>\n      </div>\n    `}}customElements.define("stat-card",StatCard);